pub fn split_content(content: &str) -> (Vec<String>, Vec<String>) { let lines: Vec<&str> = content.lines().collect(); let tags: Vec<String> = lines.iter().filter(|line| line.starts_with("#")).map(|s| s.to_string()).collect(); let sentences: Vec<String> = lines.iter().filter(|line| !line.starts_with("#") && !line.trim().is_empty()).map(|s| s.to_string()).collect(); (tags, sentences) }
